  get filteredVideos(): Video[] {
    if (this.selectedChip === 'Exploring') return this.videos;
    if (!this.selectedChip) return this.videos;
    return this.videos.filter(v => v.categories.includes(this.selectedChip!));
  }

  toggleChip(chip: string) {
    if (chip === 'Exploring') {
      this.selectedChip = 'Exploring';
      return;
    }
    this.selectedChip = this.selectedChip === chip ? 'Exploring' : chip;
  }
